<script>
	import BecomeMember from '@/components/parts/BecomeMember2'
	import MarqueeText from "vue-marquee-text-component";

	export default {
		name: "Contact_box",
		components: {
			BecomeMember,
			MarqueeText
		},
		data() {
			return {
				title: null,
				type: "contact"
			}
		},
		async mounted() {
		
		},
		computed: {
			contentData(){ 
				return this.$store.getters.mainPageData;
			},
		},
		watch: {
			'contentData.contact_title':{
				immediate: true,
				handler: function(){
					if(this.contentData){
						this.title = this.contentData.contact_title.split("\n");
					} else {
						this.title = '';
					}
				}
			}
		}, 
		methods: {
		
		}
	}
